sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var oauthRequestorSysId =  current.oauth_requestor_profile.getAttribute('requester_id');
	var oauthProfileId = current.oauth_requestor_profile.getAttribute('oauth_entity_profile');	
	var requestor_context = 'oauth_2_0_credentials';
	var grantType = 'client_credentials';

	var OauthUtilFirstAdvantageToken = new GlideAjax('x_snc_fadv.OauthUtilFirstAdvantageToken');
	OauthUtilFirstAdvantageToken.addParam('sysparm_name', 'initiateCCTokenFlow');
	OauthUtilFirstAdvantageToken.addParam('oauth_requestor', oauthRequestorSysId);
	OauthUtilFirstAdvantageToken.addParam('oauth_requestor_context', requestor_context );
	OauthUtilFirstAdvantageToken.addParam('oauth_provider_profile', oauthProfileId);
	OauthUtilFirstAdvantageToken.addParam('oauth_provider_id', oauth_provider_id);
	OauthUtilFirstAdvantageToken.getXML(parseAjaxResponse);
	function parseAjaxResponse(response) {
		var result = response.responseXML.getElementsByTagName("result");
		var isToken = result[0].getAttribute("isToken");
		var responseCode = result[0].getAttribute("responseCode");
		var errorMessage = result[0].getAttribute("errorMessage");
		if(isToken  == 'true' || isToken  == 'True' || isToken  == 'TRUE') {

			gs.addInfoMessagege('OAuth token flow completed successfully and recieved the access tokens and refresh tokens');	
		} else {
			gs.addErrorMessage('OAuth flow failed. Verify the configurations and try again. Error detail:' + errorMessage);

		}
	}



})(current, previous);
