sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var autho_header = request.getHeader('authorization');
	var code = new x_snc_fadv.FirstAdvantageWebhookUtilswithStagingScript().validateWebhookRequest(autho_header);
	if(code == "200"){  
	var inviteDetails = new x_snc_fadv.FirstAdvantageWebhookUtilswithStagingScript().validateInputs("Webhook Response", request.body.data);
	var applicant_id = new x_snc_fadv.FirstAdvantageWebhookUtilswithStagingScript().validateInputs("Invite ID", inviteDetails.order.application_details.applicant_id);
	var candidate_id = "";
	var req_number = "";
	var quote_backs = inviteDetails.order.meta_data.quote_backs;
	var client_references = inviteDetails.order.meta_data.client_references;
	var application_event = inviteDetails.order.application_details.application_event;
	var application_status = inviteDetails.order.application_details.application_status;


	for(var i=0; i<=quote_backs.length; i++){
		var obj = quote_backs[i];
		if(obj.name == 'candidate_id'){

			candidate_id = new x_snc_fadv.FirstAdvantageWebhookUtilswithStagingScript().validateInputs("Candidate ID", obj.value);
			break;
		}
	}

	var inputsPayload = {
		applicant_id : applicant_id,
		invite_status : inviteDetails.order.application_details.application_status,
		invite_url : inviteDetails.order.application_details.application_url,
		candidate_id : candidate_id,
		application_status : application_status,
		application_event : application_event
	};

	var cr =[];
	for(var key in client_references){
		if(client_references[key]){
			cr.push(client_references[key].toString());
		}
	}

	inputsPayload['client_references'] = cr.toString();
	new x_snc_fadv.FirstAdvantageWebhookUtilswithStagingScript().processWebhookInvitePayload(inputsPayload);

	return("ok");
	} else {
	return 500;
	}
})(request, response);
